import React, { useState } from 'react';
import axios from 'axios';
import './OpenAIComponent.css'; // Import your CSS file

const OpenAIComponent = () => {
    const [userInput, setUserInput] = useState('');
    const [response, setResponse] = useState('');
    const [jsonData, setJsonData] = useState('');
    const [isEditing, setIsEditing] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const result = await axios.post(
                'https://api.openai.com/v1/chat/completions',
                {
                    model: "gpt-4",
                    messages: [{ role: "user", content: userInput }],
                },
                {
                    headers: {
                        'Authorization': `Bearer ${process.env.REACT_APP_OPENAI_API_KEY}`,
                        'Content-Type': 'application/json',
                    },
                }
            );
            setResponse(result.data.choices[0].message.content);
        } catch (error) {
            console.error("Error fetching data from OpenAI:", error);
            setResponse("Error fetching response");
        }
    };

    const handleGenerateRequirements = async () => {
        // Fetch the JSON file from the local server
        try {
            const response = await fetch('http://localhost:8080/json/soccer.json'); // Updated path
            const data = await response.json();
            setJsonData(JSON.stringify(data, null, 2)); // Format JSON for readability
            setIsEditing(false); // Set editing mode off initially
        } catch (error) {
            console.error("Error fetching JSON file:", error);
            setJsonData("Error fetching JSON data");
        }
    };

    const handleEditToggle = () => {
        setIsEditing(!isEditing);
    };

    const handleJsonChange = (e) => {
        setJsonData(e.target.value);
    };

    return (
        <div className="openai-container">
            <form className="openai-form" onSubmit={handleSubmit}>
                <input
                    className="openai-input"
                    type="text"
                    value={userInput}
                    onChange={(e) => setUserInput(e.target.value)}
                    placeholder="Ask me anything..."
                />
                <button className="openai-button" type="submit">Send</button>
            </form>
            <button className="openai-button generate" onClick={handleGenerateRequirements}>Generate Requirements</button>
            <button className="openai-button" onClick={handleEditToggle}>
                {isEditing ? 'Save Changes' : 'Edit JSON'}
            </button>
            <div className="openai-response">
                <h3>Response:</h3>
                <pre>{response}</pre>
            </div>
            <div className="json-display">
                <h3>JSON Data:</h3>
                {isEditing ? (
                    <textarea
                        value={jsonData}
                        onChange={handleJsonChange}
                        rows="10"
                        cols="50"
                    />
                ) : (
                    <pre style={{ color: 'black' }}>{jsonData}</pre> // Set JSON data color to black
                )}
            </div>
        </div>
    );
};

export default OpenAIComponent;
