import React, { useState } from 'react';
import axios from 'axios';
import './OpenAIComponent.css';

// Define the Feature type with children and isOpen state
interface Feature {
  id: number;
  name: string;
  children: Feature[];
  isOpen: boolean;
}

const OpenAIComponent = () => {
  const [userInput, setUserInput] = useState('');
  const [output, setOutput] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [selectedFeatures, setSelectedFeatures] = useState([]);
  const [featureList, setFeatureList] = useState(features);

  // Function to handle OpenAI API call
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const result = await axios.post(
        'https://api.openai.com/v1/chat/completions',
        {
          model: 'gpt-4',
          messages: [{ role: 'user', content: userInput }],
        },
        {
          headers: {
            Authorization: `Bearer ${process.env.REACT_APP_OPENAI_API_KEY}`,
            'Content-Type': 'application/json',
          },
        }
      );
      setOutput(result.data.choices[0].message.content);
      setIsEditing(false); // Ensure editing is disabled after fetching response
    } catch (error) {
      console.error('Error fetching data from OpenAI:', error);
      setOutput('Error fetching response');
    }
  };

  // Function to fetch JSON file from the local server
  const handleGenerateRequirements = async () => {
    if (userInput.toLowerCase().includes('soccer')) {
      try {
        const response = await fetch('http://localhost:8080/json/soccer.json');
        const data = await response.json();
        setOutput(JSON.stringify(data, null, 2));
        setIsEditing(true); // Enable editing when displaying JSON data
      } catch (error) {
        console.error('Error fetching JSON file:', error);
        setOutput('Error fetching JSON data');
      }
    } else if (userInput.toLowerCase().includes('shopping')) {
      try {
        const response = await fetch('http://localhost:8080/json/shopping.json');
        const data = await response.json();
        setOutput(JSON.stringify(data, null, 2));
        setIsEditing(true); // Enable editing when displaying JSON data
      } catch (error) {
        console.error('Error fetching JSON file:', error);
        setOutput('Error fetching JSON data');
      }
    } else {
      setOutput('No matching JSON file found for the given input.');
    }
  };

  const handleJsonChange = (e) => {
    setOutput(e.target.value);
  };

  const handleFeatureSelect = (feature: Feature) => {
    if (selectedFeatures.includes(feature)) {
      setSelectedFeatures(selectedFeatures.filter((f) => f !== feature));
    } else {
      setSelectedFeatures([...selectedFeatures, feature]);
    }
  };

  const handleToggle = (id: number) => {
    const newFeatures = featureList.map((feature) => {
      if (feature.id === id) {
        return { ...feature, isOpen: !feature.isOpen };
      }
      return feature;
    });
    setFeatureList(newFeatures);
  };

  const renderFeatures = (features: Feature[], level: number) => {
    return features.map((feature) => (
      <div key={feature.id} className={`ml-${level * 4}`}>
        <input
          type="checkbox"
          checked={selectedFeatures.includes(feature)}
          onChange={() => handleFeatureSelect(feature)}
        />
        <span className="ml-2">{feature.name}</span>
        {feature.isOpen && (
          <div className="ml-4">
            {renderFeatures(feature.children, level + 1)}
          </div>
        )}
        <button onClick={() => handleToggle(feature.id)}>
          {feature.isOpen ? 'Hide' : 'Show'} children
        </button>
      </div>
    ));
  };

  return (
    <div className="openai-container">
      <form className="openai-form" onSubmit={handleSubmit}>
        <div className="openai-input-container">
          <input
            className="openai-input"
            type="text"
            value={userInput}
            onChange={(e) => setUserInput(e.target.value)}
            placeholder="Ask me anything..."
          />
          <div className="send-arrow" onClick={handleSubmit}>
            ➤
          </div>
        </div>
      </form>

      <button className="openai-button generate" onClick={handleGenerateRequirements}>
        Generate Requirements
      </button>

      <div className="output-display">
        <h3>Output:</h3>
        {isEditing ? (
          <textarea
            value={output}
            onChange={handleJsonChange}
            rows="10"
            cols="50"
          />
        ) : (
          <pre style={{ color: 'yellow', fontFamily: 'monospace', fontSize: '16px' }}>
            {output}
          </pre>
        )}
      </div>

      {/* Feature List Section */}
      <div className="feature-list">
        <h3>Hierarchical Feature List:</h3>
        {renderFeatures(featureList, 1)}
      </div>
    </div>
  );
};

const features: Feature[] = [
  {
    id: 1,
    name: 'Authentication',
    children: [
      { id: 2, name: 'Login', children: [], isOpen: false },
      { id: 3, name: 'Register', children: [], isOpen: false },
    ],
    isOpen: false,
  },
  {
    id: 4,
    name: 'Payment Gateway',
    children: [
      { id: 5, name: 'Stripe', children: [], isOpen: false },
      { id: 6, name: 'PayPal', children: [], isOpen: false },
    ],
    isOpen: false,
  },
  {
    id: 7,
    name: 'Social Media Integration',
    children: [
      { id: 8, name: 'Facebook', children: [], isOpen: false },
      { id: 9, name: 'Twitter', children: [], isOpen: false },
    ],
    isOpen: false,
  },
];

export default OpenAIComponent;
