import React, { useState } from 'react';
import axios from 'axios';
import './OpenAIComponent.css'; // Import your CSS file

const OpenAIComponent = () => {
    const [userInput, setUserInput] = useState('');
    const [response, setResponse] = useState('');
    const [jsonData, setJsonData] = useState('');
    const [isEditingResponse, setIsEditingResponse] = useState(false);
    const [isEditingJson, setIsEditingJson] = useState(false);

    // Function to handle OpenAI API call
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const result = await axios.post(
                'https://api.openai.com/v1/chat/completions',
                {
                    model: "gpt-4",
                    messages: [{ role: "user", content: userInput }],
                },
                {
                    headers: {
                        'Authorization': `Bearer ${process.env.REACT_APP_OPENAI_API_KEY}`,
                        'Content-Type': 'application/json',
                    },
                }
            );
            setResponse(result.data.choices[0].message.content);
        } catch (error) {
            console.error("Error fetching data from OpenAI:", error);
            setResponse("Error fetching response");
        }
    };

    // Function to fetch JSON file from the local server
    const handleGenerateRequirements = async () => {
        try {
            const response = await fetch('http://localhost:8080/json/soccer.json');
            const data = await response.json();
            setJsonData(JSON.stringify(data, null, 2));
            setIsEditingJson(false);
        } catch (error) {
            console.error("Error fetching JSON file:", error);
            setJsonData("Error fetching JSON data");
        }
    };

    const handleEditResponseToggle = () => {
        setIsEditingResponse(!isEditingResponse);
    };

    const handleEditJsonToggle = () => {
        setIsEditingJson(!isEditingJson);
    };

    const handleJsonChange = (e) => {
        setJsonData(e.target.value);
    };

    const handleResponseChange = (e) => {
        setResponse(e.target.value);
    };

    return (
        <div className="openai-container">
            <form className="openai-form" onSubmit={handleSubmit}>
                <div className="openai-input-container">
                    <input
                        className="openai-input"
                        type="text"
                        value={userInput}
                        onChange={(e) => setUserInput(e.target.value)}
                        placeholder="Ask me anything..."
                    />
                    <div className="send-arrow" onClick={handleSubmit}>
                        âž¤
                    </div>
                </div>
            </form>

            <button className="openai-button generate" onClick={handleGenerateRequirements}>
                Generate Requirements
            </button>

            <div className="openai-response" onClick={handleEditResponseToggle} style={{ cursor: 'pointer' }}>
                <h3>Response:</h3>
                {isEditingResponse ? (
                    <textarea
                        value={response}
                        onChange={handleResponseChange}
                        rows="10"
                        cols="50"
                    />
                ) : (
                    <pre style={{ fontSize: '16px' }}>{response}</pre>
                )}
            </div>

            <div className="json-display" onClick={handleEditJsonToggle} style={{ cursor: 'pointer' }}>
                <h3>JSON Data:</h3>
                {isEditingJson ? (
                    <textarea
                        value={jsonData}
                        onChange={handleJsonChange}
                        rows="10"
                        cols="50"
                    />
                ) : (
                    <pre style={{ color: 'white', fontFamily: 'monospace', fontSize: '16px' }}>{jsonData}</pre>
                )}
            </div>
        </div>
    );
};

export default OpenAIComponent;
