import React, { useState } from 'react';
import axios from 'axios';

const OpenAIComponent = () => {
    const [userInput, setUserInput] = useState('');
    const [response, setResponse] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const result = await axios.post(
                'https://api.openai.com/v1/chat/completions',
                {
                    model: "gpt-4",
                    messages: [{ role: "user", content: userInput }],
                },
                {
                    headers: {
                        'Authorization': `Bearer ${process.env.REACT_APP_OPENAI_API_KEY}`,
                        'Content-Type': 'application/json',
                    },
                }
            );
            setResponse(result.data.choices[0].message.content);
        } catch (error) {
            console.error("Error fetching data from OpenAI:", error);
            setResponse("Error fetching response");
        }
    };

    const handleGenerateRequirements = async () => {
        const prompt = `Generate a JSON file for requirements based on the following input: "${userInput}"`;
        await handleSubmit({ preventDefault: () => {} }); // Call handleSubmit to make an API request
        setUserInput(prompt); // Set the prompt for generating requirements
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    value={userInput}
                    onChange={(e) => setUserInput(e.target.value)}
                    placeholder="Ask me anything..."
                />
                <button type="submit">Send</button>
            </form>
            <button onClick={handleGenerateRequirements}>Generate Requirements</button>
            <div>
                <h3>Response:</h3>
                <pre>{response}</pre>
            </div>
        </div>
    );
};

export default OpenAIComponent;
